  // Get customer profile from the Extract Customer Profile node
  const customerProfile = $('Extract Customer Profile').first().json;

  // Initialize broker data
  let brokerData = null;

  // Check which path we came from
  // Path 1: Existing broker (hasbroker = true)
  // Look for the full broker details from "Execute a SQL query3" node
  try {
    const existingBroker = $('Execute a SQL query3').first().json;
    if (existingBroker && existingBroker.id) {
      brokerData = {
        id: existingBroker.id,
        name: existingBroker.name,
        slug: existingBroker.slug,
        photoUrl: existingBroker.photo_url,
        personalityType: existingBroker.personality_type,
        voiceDescription: existingBroker.voice_description,
        communicationStyle: existingBroker.communication_style,
        approachStyle: existingBroker.approach_style,
        favoritePhrases: existingBroker.favorite_phrases || [],
        speakingSpeed: existingBroker.speaking_speed,
        voiceModel: existingBroker.voice_model
      };
    }
  } catch (e) {
    // Node doesn't exist or wasn't executed - try the other path
  }

  // Path 2: New broker assignment (hasbroker = false)
  if (!brokerData) {
    try {
      const newBroker = $('Assign Best Broker 2').first().json;
      if (newBroker) {
        brokerData = {
          id: newBroker.assigned_broker_id || newBroker.id,
          name: newBroker.name,
          slug: newBroker.slug,
          photoUrl: newBroker.photo_url,
          personalityType: newBroker.personality_type,
          voiceDescription: newBroker.voice_description,
          communicationStyle: newBroker.communication_style,
          approachStyle: newBroker.approach_style,
          favoritePhrases: newBroker.favorite_phrases || [],
          speakingSpeed: newBroker.speaking_speed,
          voiceModel: newBroker.voice_model
        };
      }
    } catch (e) {
      // This path wasn't executed either
    }
  }

  // If still no broker data, check the inputs directly
  if (!brokerData) {
    // Look through all inputs for broker data
    for (const input of inputs) {
      const data = input.json;
      // Check if this looks like broker data
      if (data.id && data.name && (data.personality_type || data.voice_model)) {
        brokerData = {
          id: data.id,
          name: data.name,
          slug: data.slug,
          photoUrl: data.photo_url,
          personalityType: data.personality_type,
          voiceDescription: data.voice_description,
          communicationStyle: data.communication_style,
          approachStyle: data.approach_style,
          favoritePhrases: data.favorite_phrases || [],
          speakingSpeed: data.speaking_speed,
          voiceModel: data.voice_model
        };
        break;
      }
    }
  }

  // Return merged context
  return {
    broker: brokerData,
    customer: customerProfile,
    conversationId: customerProfile?.conversationId,
    messageCount: customerProfile?.messageCount || 0
  };

try {
    const conversationData = $input.first().json;
    const brokerContext = $('Merge Broker Context').first().json;

    const messages = conversationData.messages || [];

    // Build conversation history
    const conversationHistory = [];
    for (let i = 0; i < Math.min(10, messages.length); i++) {
      const msg = messages[messages.length - 1 - i];
      if (!msg.private) {
        let role = 'system';
        if (msg.message_type === 0) {
          role = 'user';
        } else if (msg.message_type === 1) {
          role = 'assistant';
        }
        conversationHistory.unshift({
          role: role,
          content: msg.content
        });
      }
    }

    // Identify topics
    const topicsDiscussed = [];
    const messageTexts = messages.map(m => String(m.content || '').toLowerCase()).join(' ');

    if (messageTexts.indexOf('rate') !== -1) topicsDiscussed.push('interest rates');
    if (messageTexts.indexOf('afford') !== -1) topicsDiscussed.push('affordability');
    if (messageTexts.indexOf('cpf') !== -1) topicsDiscussed.push('CPF usage');
    if (messageTexts.indexOf('loan') !== -1) topicsDiscussed.push('loan details');
    if (messageTexts.indexOf('refinanc') !== -1) topicsDiscussed.push('refinancing');

    // Use the actual current message from customer
    const currentMsg = brokerContext.customer?.currentMessage || 'I need help with my mortgage';

    return {
      broker: brokerContext.broker || null,
      customer: brokerContext.customer || null,
      conversationHistory: conversationHistory,
      topicsDiscussed: topicsDiscussed,
      totalMessages: messages.length,
      currentMessage: currentMsg
    };

  } catch (error) {
    return {
      error: error.message,
      stack: error.stack
    };
  }

// Get inputs
  const aiData = $input.first().json;
  const processHistory = $('Process History').first().json;

  // Extract data
  const response = aiData.message.content;
  const customer = processHistory.customer;
  const broker = processHistory.broker;
  const messageCount = processHistory.totalMessages;
  const userMessage = customer.currentMessage.toLowerCase();

  // Initialize
  let shouldHandoff = false;
  let handoffReason = 'none';
  let urgencyLevel = 'normal';

  // Check for handoff keywords
  const handoffKeywords = [
    'ready to apply', 'apply now', 'lets proceed',
    'speak to human', 'real person', 'human agent',
    'sign up', 'start application', 'move forward'
  ];

  for (const keyword of handoffKeywords) {
    if (userMessage.includes(keyword)) {
      shouldHandoff = true;
      handoffReason = 'Customer explicitly requested to proceed or speak with human';
      urgencyLevel = 'high';
      break;
    }
  }

  // Check high-value lead at optimal handoff point
  if (!shouldHandoff && messageCount >= 7 && customer.leadScore >= 80) {
    shouldHandoff = true;
    handoffReason = 'High-value lead (score: ' + customer.leadScore + ') ready for conversion after ' + messageCount + ' messages';
    urgencyLevel = 'medium';
  }

  // Check urgent timeline
  if (!shouldHandoff && customer.timeline === 'urgent' && messageCount >= 5) {
    shouldHandoff = true;
    handoffReason = 'Urgent timeline customer engaged for ' + messageCount + ' messages';
    urgencyLevel = 'high';
  }

  // Check complex situations
  const complexKeywords = ['divorce', 'bankruptcy', 'foreigner', 'overseas', 'complicated'];
  for (const keyword of complexKeywords) {
    if (userMessage.includes(keyword)) {
      shouldHandoff = true;
      handoffReason = 'Complex situation requiring specialist attention';
      urgencyLevel = 'medium';
      break;
    }
  }

  // Check frustration
  const frustrationKeywords = ['frustrated', 'not helpful', 'speak to someone else', 'waste time'];
  for (const keyword of frustrationKeywords) {
    if (userMessage.includes(keyword)) {
      shouldHandoff = true;
      handoffReason = 'Customer showing frustration - immediate attention needed';
      urgencyLevel = 'urgent';
      break;
    }
  }

  // Add handoff message if needed
  let finalResponse = response;
  if (shouldHandoff) {
    const handoffMessage = ' I can see you are ready to take the next step! Let me connect you with our senior mortgage specialist who can process your application immediately and secure these rates for you.';
    finalResponse = response + '\n\n---\n' + handoffMessage;
  }

  // Return result
  return {
    aiResponse: finalResponse,
    shouldHandoff: shouldHandoff,
    handoffReason: handoffReason,
    urgencyLevel: urgencyLevel,
    conversationId: customer.conversationId,
    brokerId: broker.id,
    brokerName: broker.name,
    messageCount: messageCount + 1
  };

