// Extract customer profile from conversation
const conversation = $input.first().json.conversation;
const message = $input.first().json.message;
const attributes = conversation.custom_attributes || {};

// Build customer profile
const customerProfile = {
  // Identifiers
  conversationId: conversation.id,
  contactId: conversation.contact_id,
  messageId: message.id,

  // Customer Data
  name: attributes.name || conversation.contact?.name || 'Customer',
  email: attributes.email || conversation.contact?.email,
  phone: attributes.phone || conversation.contact?.phone_number,

  // Lead Qualification
  leadScore: parseInt(attributes.lead_score) || 50,
  loanType: attributes.loan_type || 'new_purchase',
  propertyType: attributes.property_category || 'hdb',
  monthlyIncome: parseFloat(attributes.monthly_income) || 5000,
  loanAmount: parseFloat(attributes.loan_amount) || 0,
  timeline: attributes.purchase_timeline || 'flexible',
  employmentType: attributes.employment_type || 'employed',

  // Message Context
  currentMessage: message.content,
  messageCount: parseInt(attributes.message_count) || 0,

  // Previous Assignment
  previousBrokerId: attributes.assigned_broker_id,
  previousBrokerName: attributes.assigned_broker_name
};

return customerProfile;

// Merge broker data from either path
const brokerData = $input.all()[0].json;
const customerProfile = $input.all()[1].json;

// Prepare broker context
return {
  broker: {
    id: brokerData.id || brokerData.broker_id,
    name: brokerData.name || brokerData.broker_name,
    slug: brokerData.slug || brokerData.broker_slug,
    photoUrl: brokerData.photo_url,
    personalityType: brokerData.personality_type,
    voiceDescription: brokerData.voice_description,
    communicationStyle: brokerData.communication_style,
    approachStyle: brokerData.approach_style,
    favoritePhrases: brokerData.favorite_phrases || [],
    speakingSpeed: brokerData.speaking_speed,
    voiceModel: brokerData.voice_model
  },
  customer: customerProfile,
  conversationId: customerProfile.conversationId,
  messageCount: customerProfile.messageCount
};

// Build conversation history for OpenAI
const messages = $json.payload || [];
const brokerContext = $input.all()[1].json;

// Get last 10 messages for context
const conversationHistory = messages
  .slice(-10)
  .filter(msg => !msg.private)
  .map(msg => ({
    role: msg.message_type === 0 ? 'user' : 'assistant',
    content: msg.content
  }));

// Identify topics discussed
const topicsDiscussed = [];
const messageTexts = messages.map(m => m.content.toLowerCase()).join(' ');

if (messageTexts.includes('rate')) topicsDiscussed.push('interest rates');
if (messageTexts.includes('afford')) topicsDiscussed.push('affordability');
if (messageTexts.includes('cpf')) topicsDiscussed.push('CPF usage');
if (messageTexts.includes('loan')) topicsDiscussed.push('loan details');
if (messageTexts.includes('property')) topicsDiscussed.push('property type');

return {
  ...brokerContext,
  conversationHistory,
  topicsDiscussed,
  totalMessages: messages.length
};

