// Analyze for handoff triggers
const response = $json.choices[0].message.content;
const customer = $input.all()[1].json.customer;
const broker = $input.all()[1].json.broker;
const messageCount = $input.all()[1].json.messageCount;
const userMessage = customer.currentMessage.toLowerCase();

let shouldHandoff = false;
let handoffReason = '';
let urgencyLevel = 'normal';

// Check handoff triggers
const handoffKeywords = [
  'speak to human', 'real person', 'human agent',
  'ready to apply', 'apply now', 'let\'s proceed',
  'sign up', 'start application', 'move forward'
];

// Direct request for human
if (handoffKeywords.some(keyword => userMessage.includes(keyword))) {
  shouldHandoff = true;
  handoffReason = `Customer explicitly requested to proceed or speak with human`;
  urgencyLevel = 'high';
}
// High-value lead at optimal handoff point
else if (messageCount >= 7 && customer.leadScore >= 80) {
  shouldHandoff = true;
  handoffReason = `High-value lead (score: ${customer.leadScore}) ready for conversion after ${messageCount} messages`;
  urgencyLevel = 'medium';
}
// Urgent timeline
else if (customer.timeline === 'immediate' && messageCount >= 5) {
  shouldHandoff = true;
  handoffReason = `Urgent timeline customer engaged for ${messageCount} messages`;
  urgencyLevel = 'high';
}
// Complex situation
else if (userMessage.includes('divorce') || userMessage.includes('bankruptcy') || 
         userMessage.includes('foreigner') || userMessage.includes('overseas')) {
  shouldHandoff = true;
  handoffReason = 'Complex situation requiring specialist attention';
  urgencyLevel = 'medium';
}
// Frustration detected
else if (userMessage.includes('frustrated') || userMessage.includes('not helpful') ||
         userMessage.includes('speak to someone else')) {
  shouldHandoff = true;
  handoffReason = 'Customer showing frustration - immediate attention needed';
  urgencyLevel = 'urgent';
}

// Prepare handoff message if needed
let finalResponse = response;
if (shouldHandoff) {
  const handoffMessage = `\n\n---\nü§ù I can see you're ready to take the next step! Let me connect you with our senior mortgage specialist who can process your application immediately and secure these rates for you. They'll be with you in just a moment.`;
  finalResponse = response + handoffMessage;
}

return {
  aiResponse: finalResponse,
  shouldHandoff,
  handoffReason,
  urgencyLevel,
  conversationId: customer.conversationId,
  brokerId: broker.id,
  brokerName: broker.name,
  messageCount: messageCount + 1
};

